import React, { useEffect, useState } from "react";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import { FaRegEdit } from "react-icons/fa";
import apiManager from "../api/apiManager";

const DriverList = (props) => {
  const dates = props.dates;
  const routes = props.routes;
  const chosenDateId = props.chosenDateId;
  const chosenDateName = props.chosenDateName;
  const chosenRoute = props.chosenRoute;
  const handleChosenDateChange = props.handleChosenDateChange;
  const handleChosenRouteChange = props.handleChosenRouteChange;

  const [assignments, setAssignments] = useState([]);

  const getAssignments = () => {
    apiManager.getAllType("assignments").then((r) => {
      r.sort((a, b) => a.driver.name.localeCompare(b.driver.name));
      setAssignments(r);
    });
  };

  const [searchField, setSearchField] = useState("");

  const handleSearchChange = (e) => {
    const stateToChange = { ...searchField };
    stateToChange[e.target.id] = e.target.value.toLowerCase();
    setSearchField(stateToChange);
  };

  const filteredAssignments = assignments
    .filter((each) => each.route.name.includes(chosenRoute))
    .filter((each1) => each1.date.date.includes(chosenDateName))
    .filter(
      (each2) =>
        each2.driver.name.toLowerCase().includes(searchField) ||
        each2.vehicle.company.toLowerCase().includes(searchField) ||
        each2.vehicle.number.includes(searchField)
    )
    .sort((a, b) => a.route.name.localeCompare(b.route.name))
    .sort((a, b) => a.date.date.localeCompare(b.date.date));



  useEffect(() => {
    getAssignments();
  }, []);

  return (
    <>
      <Typography component="h1" variant="h5">
        Driver List
      </Typography>
      <div className="drop-downs">
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <InputLabel>Date:</InputLabel>
            <Select
              id="dateId"
              native
              onChange={handleChosenDateChange}
              fullWidth
              required
              value={chosenDateId}
            >
              <option aria-label="None" value="" data-name="">
                All Dates
              </option>
              {dates ? (
                dates.map((date) => (
                  <option key={date.id} value={date.id} data-name={date.date}>
                    {date.date}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Route:</InputLabel>
            <Select
              id="routeId"
              native
              onChange={handleChosenRouteChange}
              fullWidth
              required
              value={chosenRoute}
            >
              <option aria-label="None" value="">
                All Routes
              </option>
              {routes ? (
                routes.map((route) => (
                  <option key={route.id} value={route.name}>
                    {route.name} {route.description}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel htmlFor="age-native-simple">Search:</InputLabel>
            <TextField
              type="text"
              id="keyword"
              fullWidth
              onChange={handleSearchChange}
              value={searchField}
            />
          </Grid>
          {/* {mode === false ? (
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                handleSearch();
              }}
              style={{ textDecoration: "none" }}
            >
              Search
            </Button>
          ) : (
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                clearSearch();
              }}
              style={{ textDecoration: "none" }}
            >
              Clear Search
            </Button>
          )} */}
        </Grid>
      </div>
      <TableContainer>
        <Table size="small">
          <TableHead>
            <TableRow>
              {chosenDateId === "" ? <TableCell>Date</TableCell> : null}
              <TableCell>
                Name<div>Number</div>
              </TableCell>
              <TableCell>
                Company<div>Veh Number</div>
              </TableCell>
              {chosenRoute === "" ? <TableCell>Route</TableCell> : null}
              <TableCell>
                Local<div>ADA</div>
              </TableCell>
              <TableCell>Notes</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          {mode === false ? (
            <TableBody>
              {filteredAssignments.map((assignment) => (
                <TableRow key={assignment.id}>
                  {chosenDateId === "" ? (
                    <TableCell>{assignment.date.date.slice(5)}</TableCell>
                  ) : null}
                  <TableCell className="list_name">
                    {assignment.driver.name}
                    <div>{assignment.driver.phone_number}</div>
                  </TableCell>
                  <TableCell className="list_phone">
                    {assignment.vehicle.company}
                    <div>{assignment.vehicle.number}</div>
                  </TableCell>
                  {chosenRoute === "" ? (
                    <TableCell>{assignment.route.name}</TableCell>
                  ) : null}
                  {assignment.driver.isLocal === true ? (
                    <TableCell className="is_local">Local</TableCell>
                  ) : (
                    <TableCell></TableCell>
                  )}
                  <TableCell className="list_notes">
                    {assignment.driver.notes}
                  </TableCell>
                  <TableCell>
                    <span className="action-icon">
                      <FaRegEdit
                        style={{ fontSize: 20 }}
                        onClick={() =>
                          props.history.push(
                            `/driver/edit/${assignment.driver_id}/${assignment.driver_id}`
                          )
                        }
                      />
                    </span>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          ) : (
            <TableBody>
              {searchResults.map((assignment) => (
                <TableRow key={assignment.id}>
                  {chosenDateId === "" ? (
                    <TableCell>{assignment.date.date.slice(5)}</TableCell>
                  ) : null}
                  <TableCell className="list_name">
                    {assignment.driver.name}
                    <div>{assignment.driver.phone_number}</div>
                  </TableCell>
                  <TableCell className="list_phone">
                    {assignment.vehicle.company}
                    <div>{assignment.vehicle.number}</div>
                  </TableCell>
                  {chosenRoute === "" ? (
                    <TableCell>{assignment.route.name}</TableCell>
                  ) : null}
                  {assignment.driver.isLocal === true ? (
                    <TableCell className="is_local">Local</TableCell>
                  ) : (
                    <TableCell></TableCell>
                  )}
                  <TableCell className="list_notes">
                    {assignment.driver.notes}
                  </TableCell>
                  <TableCell>
                    <span className="action-icon">
                      <FaRegEdit
                        style={{ fontSize: 20 }}
                        onClick={() =>
                          props.history.push(
                            `/driver/edit/${assignment.driver_id}/${assignment.driver_id}`
                          )
                        }
                      />
                    </span>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          )}
        </Table>
      </TableContainer>
    </>
  );
};

export default DriverList;
