import React, { useState, useEffect } from "react";
import apiManager from "./api/apiManager";
import EntryRow from "./EntryRow"

const LocationLog = (props) => {
  const locations = props.locations;
  const chosen_location = props.chosen_location;

  return (
    <>
      <div>
        <select
          id="locationId"
          onChange={props.handleLocationChange}
          value={props.chosen_location}
          className="location_picker"
        >
          {props.locations.map((location) => (
            <option key={location.id} value={location.id}>
              {location.name}
            </option>
          ))}
        </select>
      </div>
      <div className="location_log_header">
        <h2>Entries from certain location</h2>
      </div>
      <div className="list-container">
        {locations
          .filter((location) => location.id === chosen_location)
          .map((location) => (
            <EntryRow key={location.id} location={location} {...props} />
          ))}
      </div>
    </>
  );
};

export default LocationLog;
