import React, { useState, useEffect } from "react";
import apiManager from "../api/apiManager";

const EntryCard = (props) => {
  const entry = props.entry;
  const [reload, setReload] = useState(false);

  const deleteThisEntry = (id) => {
    apiManager.deleteEntry(id).then(() => {
      setReload(!reload);
    });
  };

  useEffect(() => {
    apiManager.getAllEntries().then((r) => {
      setEntries(r);
    });
  }, [reload]);

  return (
    <>
      <div>
        <span>{entry.time.slice(0, -3)} </span>
        <span>{entry.vehicle_number} </span>
        <span>{entry.attendee_count} </span>
        <span>
          {entry.user.first_name} {entry.user.last_name}
        </span>
        {window.sessionStorage.getItem("userID") == entry.user_id ? (
          <>
            <button onClick={() => editThisEntry(entry.id)}>Edit</button>
            <button onClick={() => deleteThisEntry(entry.id)}>Delete</button>
          </>
        ) : null}
      </div>
    </>
  );
};

export default EntryCard;
