import React, { useEffect, useState } from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import apiManager from "../api/apiManager";

const Register = (props) => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    firstName: null,
    lastName: "",
  });
  const [failedLogin, setFailedLogin] = useState(false);
  // const [failedLoginMessage, setFailedLoginMessage] = useState("false");

  const handleFieldChange = (evt) => {
    const stateToChange = { ...formData };
    stateToChange[evt.target.id] = evt.target.value;
    setFormData(stateToChange);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let errors = [];
    for (let item in formData) {
      if (formData[item].trim() === "") {
        let newItem = [];
        [...item].forEach((element) => {
          if (element === element.toUpperCase()) {
            newItem.push(" ");
          }
          newItem.push(element.toLowerCase());
        });
        errors.push(newItem.join(""));
      }
    }

    if (errors.length > 0) {
      setFailedLogin(true);
      setFailedLoginMessage(
        "The following must not only contain spaces: " + errors.join(", ")
      );
    } else {
      const user = {
        username: formData.username.trim(),
        email: formData.email.trim(),
        password: formData.password,
        first_name: formData.firstName.trim(),
        last_name: formData.lastName.trim(),
      };

      apiManager
        .register(user)
        .then((resp) => {
          if ("token" in resp) {
            props.setUserToken(resp);
            props.history.push("/");
          }
        })
        // With a 500 HTTP error, no response is given,
        // so the error must be handled with .catch
        // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Promise/catch
        .catch(() => {
          setFailedLogin(true);
          setFailedLoginMessage(
            "The username you entered is already in use, please try again."
          );
        });
    }
  };

  useEffect(() => {}, [failedLogin]);

  // let formData = {};
  // if (props.formData) {
  //   formData = props.formData;
  // }

  // const {
  //   handleSubmit,
  //   handleFieldChange,
  //   failedLogin,
  //   failedLoginMessage,
  // } = props;

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div>
        <Typography component="h1" variant="h5">
          Create new account
        </Typography>
        <form
        // className={classes.form}
        onSubmit={handleSubmit}
        autoComplete="on"
      >
        <Grid container spacing={2}>
          {/* For now, this is the most common error we will be facing */}
          {/* {props.failedLogin 
            ? <Message negative>
                <p>{props.failedLoginMessage}</p>
              </Message>
            : <></> 
          } */}
          <Grid item xs={12} sm={6}>
            <TextField
              autoComplete="fname"
              name="firstName"
              variant="outlined"
              required
              fullWidth
              id="firstName"
              label="First Name"
              autoFocus
              onChange={handleFieldChange}
              // value={isEdit ? props.formData.firstName : null}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              variant="outlined"
              required
              fullWidth
              id="lastName"
              label="Last Name"
              name="lastName"
              autoComplete="lname"
              onChange={handleFieldChange}
              // value={isEdit ? props.formData.lastName : null}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              variant="outlined"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              onChange={handleFieldChange}
              // value={isEdit ? props.formData.username : null}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              variant="outlined"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              onChange={handleFieldChange}
              // value={isEdit ? props.formData.email : null}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              variant="outlined"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={handleFieldChange}
            />
          </Grid>
        </Grid>
        <Button type="submit" fullWidth variant="contained" color="primary">
          Submit
        </Button>
      </form>
        <Grid container justify="flex-end">
          <Grid item>
            <Link to="/login" variant="body2">
              Already have an account? Sign in
            </Link>
          </Grid>
        </Grid>
      </div>
    </Container>
  );
};

export default Register;
