import React, { useState, useEffect } from "react";
import { Route, Redirect } from "react-router-dom";
import ShuttleForm from "./forms/shuttleForm";
import RouteForm from "./forms/routeForm";
import DriverForm from "./forms/driverForm";
import VehicleForm from "./forms/vehicleForm";
import AssignmentForm from "./forms/assignmentForm";
import RouteView from "./routeView/RouteView";
import apiManager from "./api/apiManager";
import "./styles.css";

const EventTranspoManager = (props) => {
  const hasUser = props.hasUser;

  const [dates, setDates] = useState([]);

  const getDates = () => {
    apiManager.getAllType("dates").then((r) => {
      r.sort((a, b) => (a.date > b.date ? -1 : 1));
      setDates(r);
    });
  };

  console.log(dates);

  useEffect(() => {
    getDates();
  }, []);

  return (
    <span>
      <span>manager</span>
      <Route exact path="/" render={() => <Redirect to="/driver/form" />} />
      <Route
        exact
        path="/shuttle/form"
        render={() => (hasUser ? <ShuttleForm /> : <Redirect to="/login" />)}
      />
      <Route
        exact
        path="/route/form"
        render={() => (hasUser ? <RouteForm /> : <Redirect to="/login" />)}
      />
      <Route
        exact
        path="/driver/form"
        render={(props) =>
          hasUser ? <DriverForm {...props} /> : <Redirect to="/login" />
        }
      />
      <Route
        exact
        path="/assignment/form"
        render={(props) =>
          hasUser ? <AssignmentForm {...props} /> : <Redirect to="/login" />
        }
      />
      <Route
        exact
        path="/vehicle/form"
        render={(props) =>
          hasUser ? <VehicleForm {...props} /> : <Redirect to="/login" />
        }
      />
      <Route
        exact
        path="/route/view"
        render={(props) =>
          hasUser ? <RouteView dates={dates} {...props} /> : <Redirect to="/login" />
        }
      />
    </span>
  );
};

export default EventTranspoManager;
