import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import Typography from "@material-ui/core/Typography";
import RouteCard from "./routeCard";

const RouteView = (props) => {
  const dates = props.dates;
  const routes = props.routes;

  const [chosenDate, setChosenDate] = useState("");

  const handleChosenDateChange = (e) => {
    setChosenDate(e.target.value);
    // setChosenDateName(e.target.options[e.target.selectedIndex].dataset.name);
  };

  // console.log("dates", dates);
  // console.log("routes", routes);
  // console.log("chosendate", chosenDate);

  const filteredDates = dates.filter((each) => each.id === parseInt(chosenDate));

  console.log("filtered", filteredDates);

  return (
    <>
      <Grid item xs={12} md={3}>
        <InputLabel>Date:</InputLabel>
        <Select
          id="date_id"
          native
          onChange={handleChosenDateChange}
          fullWidth
          required
          value={chosenDate}
        >
          <option aria-label="None" value="" data-name="">
            Choose Date
          </option>
          {dates ? (
            dates.map((date) => (
              <option
                key={date.id}
                value={parseInt(date.id)}
                data-name={date.date}
              >
                {date.date}
              </option>
            ))
          ) : (
            <></>
          )}
        </Select>
      </Grid>
      <div className="date_card">
        {routes
          .map((route) => (
            <RouteCard key={route.id} route={route} chosenDate={chosenDate}{...props} />
          ))}
      </div>
    </>
  );
};

export default RouteView;
