import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import Typography from "@material-ui/core/Typography";
import Select from "@material-ui/core/Select";
import { Link } from "react-router-dom";
import apiManager from "../api/apiManager";

const AssignmentForm = (props) => {
  const [assignment, setAssignment] = useState({
    start_time: "",
    end_time: "",
    vehicle_id: props.vehicle_id,
    driver_id: props.driver_id,
    route_id: 1,
    date_id: props.chosenDate,
  });

  const [vehicles, setVehicles] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [dates, setDates] = useState([]);

  const getAllDropDowns = () => {
    return (
      apiManager.getAllType("vehicles").then((vehiclesFromApi) => {
        vehiclesFromApi.sort((a, b) => a.company.localeCompare(b.company));
        setVehicles(vehiclesFromApi);
      }),
      apiManager.getAllType("drivers").then((driversFromApi) => {
        driversFromApi.sort((a, b) => a.name.localeCompare(b.name));
        setDrivers(driversFromApi);
      }),
      apiManager.getAllType("routes").then((routesFromApi) => {
        routesFromApi.sort((a, b) => (a.name > b.name ? 1 : -1));
        setRoutes(routesFromApi);
      }),
      apiManager.getAllType("dates").then((datesFromApi) => {
        datesFromApi.sort((a, b) => (a.number > b.number ? -1 : 1));
        setDates(datesFromApi);
      })
    );
  };

  const handleAssignmentChange = (event) => {
    const stateToChange = { ...assignment };
    if (event.target.id === "start_time" || event.target.id === "end_time") {
      stateToChange[event.target.id] = event.target.value;
    } else {
      stateToChange[event.target.id] = parseInt(event.target.value);
    }
    setAssignment(stateToChange);
  };

  useEffect(() => {
    getAllDropDowns();
  }, []);

  // get all drivers, check if driver already in system, post if not, send to vehicle form
  const handleSubmit = (e) => {
    e.preventDefault();
    apiManager.getAllType("assignments").then((assignments) => {
      const assign = assignments.find(
        (assign) =>
          assign.date_id === assignment.date_id &&
          assign.driver_id === assignment.driver_id
      );
      if (assign === undefined) {
        apiManager
          .addType("assignments", assignment)
          .then(() => props.history.push(`/routeview`));
      } else {
        alert(
          `${assign.driver.name} has already been assigned to route ${assign.route.number} on ${assign.date.date}.`
        );
      }
    });
  };

  // const submit = () => {
  //   apiManager.getAssignments().then(assignments => {
  //     const assign = assignments.find(
  //       assign =>
  //         assign.dateId === assignment.dateId &&
  //         assign.driverId === assignment.driverId
  //     );
  //     if (assign === undefined) {
  //       apiManager
  //         .addType("assignments", assignment)
  //         .then(() =>
  //           props.history.push(`/routeview`)
  //         );
  //     } else {
  //       alert(
  //         `${assign.driver.name} has already been assigned to route ${assign.route.number} on ${assign.date.date}.`
  //       );
  //     }
  //   });
  // };

  return (
    <>
      <Typography component="h1" variant="h5" className="page-header">
        Assignment Form
      </Typography>
      <form className="drop-downs" onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <InputLabel>Driver:</InputLabel>
            <Select
              id="driver_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value=""
            >
              <option aria-label="None" value="" data-name="">
                Choose Driver
              </option>
              {drivers ? (
                drivers.map((driver) => (
                  <option key={driver.id} value={parseInt(driver.id)}>
                    {driver.name}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Vehicle:</InputLabel>
            <Select
              id="vehicle_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value=""
            >
              <option aria-label="None" value="" data-name="">
                Choose Vehicle
              </option>
              {vehicles ? (
                vehicles.map((vehicle) => (
                  <option key={vehicle.id} value={parseInt(vehicle.id)}>
                    {vehicle.company} {vehicle.number}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Route:</InputLabel>
            <Select
              id="route_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value=""
            >
              <option aria-label="None" value="" data-name="">
                Choose Route
              </option>
              {routes ? (
                routes.map((route) => (
                  <option key={route.id} value={parseInt(route.id)}>
                    {route.name}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Date:</InputLabel>
            <Select
              id="date_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value=""
            >
              <option aria-label="None" value="" data-name="">
                Choose Date
              </option>
              {dates ? (
                dates.map((date) => (
                  <option key={date.id} value={parseInt(date.id)}>
                    {date.date}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel htmlFor="age-native-simple">Start Time: </InputLabel>
            <TextField
              id="start_time"
              type="time"
              fullWidth
              value={assignment.start_time}
              onChange={handleAssignmentChange}
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel htmlFor="age-native-simple">End Time: </InputLabel>
            <TextField
              id="end_time"
              type="time"
              fullWidth
              value={assignment.end_time}
              onChange={handleAssignmentChange}
            />
          </Grid>
        </Grid>
        <div className="submit-button">
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </div>
        <div className="submit-button">
          <Link to="/vehicle/form" style={{ textDecoration: "none" }}>
            <Button variant="contained" color="primary">
              Skip
            </Button>
          </Link>
        </div>
      </form>
    </>
  );
};

export default AssignmentForm;
