import React, { useState, useEffect } from "react";
import { BrowserRouter, useHistory, Route, Redirect } from "react-router-dom";
import Nav_Bar from "./Navbar";
import EntryForm from "./forms/EntryForm";
import LocationLog from "./LocationLog"
import apiManager from "./api/apiManager";

const EventTranspoTracker = (props) => {
  let history = useHistory();
  const isAuthenticated = () => sessionStorage.getItem("token") !== null;
  const [hasUser, setHasUser] = useState(isAuthenticated());
  const [userInfo, setUserInfo] = useState({});

  const setUserToken = (resp) => {
    sessionStorage.setItem("token", resp.token);
    setHasUser(isAuthenticated());
  };

  const clearUser = () => {
    sessionStorage.clear();
    setHasUser(isAuthenticated());
  };

  const [locations, setLocations] = useState([]);
  const [chosenLocation, setChosenLocation] = useState("");
  const [userLocation, setUserLocation] = useState("");

  console.log(userLocation)

  const getLocations = () => {
    apiManager.getAllLocations().then((r) => {
      setLocations(r);
    });
  };

  useEffect(() => {
    getLocations();
  }, [])

  const handleLocationChange = (e) => {
    const locationId = parseInt(e.target.value)
    setChosenLocation(locationId);
  };

  const handleUserLocationChange = (e) => {
    const locationId = e.target
    setUserLocation(locationId);
  };

  return (
    <BrowserRouter>
      <Nav_Bar />
      <Route
        exact
        path="/entry/form"
        render={(props) => (
          <EntryForm locations={locations} userLocation={userLocation} handleUserLocationChange={handleUserLocationChange} {...props} />
        )}
      />
      <Route
        exact
        path="/location/log"
        render={(props) => (
          <LocationLog locations={locations} chosenLocation={chosenLocation} handleLocationChange={handleLocationChange} {...props} />
        )}
      />
    </BrowserRouter>
  );
};

export default EventTranspoTracker;
