import React, { useEffect, useState } from "react";
import Grid from "@material-ui/core/Grid";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import DeleteOutlinedIcon from "@material-ui/icons/DeleteOutlined";
import EditIcon from "@material-ui/icons/Edit";
import apiManager from "../api/apiManager";

const DriverList = () => {
  const [drivers, setDrivers] = useState([]);

  const getDrivers = () => {
    apiManager.getAllType("drivers").then((APIResult) => {
      APIResult.sort((a, b) => a.name.localeCompare(b.name));
      setDrivers(APIResult);
    });
  };

  useEffect(() => {
    getDrivers();
  }, []);

  let route = [];
  let noRoute = [];

  // if (props.driver.assignments[0] !== undefined) {
  //   route = props.driver.assignments[0].routeId;
  // }
  // else {
  //   noRoute = "Not Assigned"
  // }

  return (
    <div className="driver_card">
      <TableContainer>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell align="right">Phone Number</TableCell>
              <TableCell align="center">Notes</TableCell>
              {/* {chosenDate === "" ? ( */}
              <TableCell align="right">Local</TableCell>
              {/* ) : null} */}
            </TableRow>
          </TableHead>
          <TableBody>
            {drivers.map((driver) => (
              <TableRow key={driver.id}>
                <TableCell className="list_name">
                  {driver.name} {"\u00A0"}
                </TableCell>
                <TableCell className="list_phone">
                  {driver.phone_number} {"\u00A0"}{" "}
                </TableCell>
                <TableCell className="list_notes">
                  {driver.notes} {"\u00A0"}
                </TableCell>
                <TableCell className="is_local">
                  {driver.local} {"\u00A0"}
                </TableCell>
                {/* <TableCell>{"\u00A0"}{driver.assignments.routeId}</TableCell> */}
                {/* <TableCell> {"\u00A0"} {route}</TableCell> */}
                {/* <TableCell className="no_route">{"\u00A0"} {noRoute}</TableCell> */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default DriverList;
