import React from "react";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";

const EntryForm = (props) => {
  const handleEntryChange = props.handleEntryChange;
  const locations = props.locations;
  const handleSubmit = props.handleSubmit;

  return (
    <>
      <div className="product-form-page">
        <h1 className="product_header">New Product Form</h1>
        <form className="product_form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <TextField
                required
                id="title"
                label="Title"
                fullWidth
                onChange={handleEntryChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                required
                type="number"
                id="price"
                label="Price"
                fullWidth
                inputProps={{ step: 0.01 }}
                onChange={handleEntryChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                required
                type="number"
                id="quantity"
                label="Quantity"
                fullWidth
                onChange={handleEntryChange}
              />
            </Grid>
            <InputLabel htmlFor="age-native-simple">Product Type</InputLabel>
            <Select
              id="product_type_id"
              native
              // value={product.product_type_id}
              onChange={handleEntryChange}
              fullWidth
              required
              label="Image URL"
            >
              <option aria-label="None" value="">
                Choose Item
              </option>
              {locations ? (
                locations.map((location) => (
                  <option key={location.id} value={location.id}>
                    {location.name}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12}>
            <button type="submit" content="Submit Entry"></button>
          </Grid>
        </form>
      </div>
    </>
  );
};

export default EntryForm;
