import React from "react";
import { Bar } from "react-chartjs-2";
import moment from "moment";
// import apiManager from "../api/apiManager";

const RouteGraph = (props) => {
  const entries = props.entriesByDate;

  // const uniqueDates = [...new Set(entries.map((entry) => entry.date))]

  if (entries[5] !== undefined) {
    if (Math.round(entries[5].time[3]) == 0) {
      console.log(`${entries[5].time}`);
    }
  }

  const roundedTimes = entries.map((entry) => {
    if (entry.time[3] == 0) {
      return `${entry.time.split(":")[0]}:${(
        (Math.round(entry.time.split(":")[1] / 10) * 10) %
        60
      ).toString()}0`;
    } else {
      return `${entry.time.split(":")[0]}:${(
        (Math.round(entry.time.split(":")[1] / 10) * 10) %
        60
      ).toString()}`;
    }
  });

  console.log("rounded times", roundedTimes);

  const state = {
    labels: roundedTimes,
    datasets: [
      {
        backgroundColor: "rgba(75,192,192,1)",
        borderColor: "rgba(0,0,0,1)",
        borderWidth: 2,
        data: entries.map((entry) => entry.attendee_count),
      },
    ],
  };

  return (
    <div>
      <Bar
        data={state}
        options={{
          title: {
            display: true,
            text: "Attendee counts over time",
            fontSize: 20,
          },
          legend: {
            display: false,
          },
          scales: {
            yAxes: [
              {
                ticks: {
                  beginAtZero: true,
                },
              },
            ],
          },
        }}
      />
    </div>
  );
};

export default RouteGraph;
