import React, { useEffect, useState } from "../../node_modules/react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import apiManager from "../api/apiManager";
import AssignmentCard from "./assignmentCard";

const RouteCard = (props) => {
  const chosenDate = props.chosenDate;
  const route = props.route;
  const [assignments, setAssignments] = useState([]);

  useEffect(() => {
    apiManager
      .getAssignmentsByDateRoute(parseInt(chosenDate), route.id)
      .then((r) => {
        setAssignments(r);
      });
  }, [chosenDate]);

  // const removeAssignment = (type, id, dateId, routeId) => {
  //   apiManager.deleteTypeWithId(type, id).then(() =>
  //     apiManager
  //       .getAssignmentsByDateRoute(dateId, routeId)
  //       .then((APIResult) => {
  //         setAssignments(APIResult);
  //       })
  //   );
  // };

  // const routeHeader = {
  //   color: props.route.color,
  //   fontSize: "larger",
  //   fontWeight: 600,
  //   marginRight: "20px"
  // };

  const routeBorder = {
    borderColor: route.color,
    borderWidth: "thin",
  };

  const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
    },
  }));

  const classes = useStyles();

  return (
    <Grid container>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          {parseInt(route.name) < 10 ? (
            <Paper className={classes.paper} style={routeBorder}>
              Route {route.name.slice(1)} {route.description}
              <div>
                {assignments.map((assignment) => (
                  <AssignmentCard
                    key={assignment.id}
                    assignment={assignment}
                    // removeAssignment={removeAssignment}
                    {...props}
                  />
                ))}
              </div>
            </Paper>
          ) : (
            <Paper className={classes.paper}>
              Route {route.name} {route.description}
              <div>
                {assignments.map((assignment) => (
                  <AssignmentCard
                    key={assignment.id}
                    assignment={assignment}
                    // removeAssignment={removeAssignment}
                    {...props}
                  />
                ))}
              </div>
            </Paper>
          )}
        </Grid>
      </Grid>
      <div className="route_heading">
        {/* <span className="assigned">
            ({assignments.length} assigned -{"\u00A0"}
            {props.route.numOfVehNeeded - assignments.length} needed)
          </span> */}
        {/* {favoriteRouteIDs.length !== 0 ? (
            <span className="route_icon" id-="unStar" onClick={unStar}>
              unstar
            </span>
          ) : null}
          {favoriteRouteIDs.length === 0 ? (
            <span className="route_icon" id="star" onClick={star}>
              star
            </span>
          ) : null} */}
      </div>
      <span
        className="add_driver_icon"
        onClick={() => props.history.push(`/add/${props.route.id}`)}
      >
        add driver
      </span>
    </Grid>
  );
};

export default RouteCard;
