import React from "react";
import { Bar } from "react-chartjs-2";

const TestA = (props) => {
  const entries = props.filteredEntries;

  const xD = entries.map((entry) => entry.time.slice(0, 4));
  const yD = entries.map((entry) => entry.attendee_count);
  const combinedA = [];
  for (let i = 0; i < entries.length; i++) {
    combinedA.push({ x: `${xD[i]}0`, y: yD[i] });
  }

  const earliestHour = parseInt(xD[0]);
  const latestHour = parseInt(xD[xD.length - 1]) + 1;

  const timeSpacedA = [];
  for (let j = earliestHour; j < latestHour; j++) {
    console.log(j)
    for (let i = 0; i < 6; i++) {
      if (earliestHour.length == 1) {
        timeSpacedA.push(`0${j}:${i}0`);
      } else {
        timeSpacedA.push(`${j}:${i}0`);
      }
    }
  }

  function barValue(x) {
    for (let each of combinedA) {
      if (x === each.x) {
        return each.y;
      }
    }
    return 0;
  }

  console.log(
    combinedA,
    timeSpacedA.map((v) => v),
    timeSpacedA.map((v) => barValue(v))
  );

  return (
    <div>
      <Bar
        type="bar"
        data={{
          labels: timeSpacedA.map((v) => v),
          datasets: [
            {
              backgroundColor: "#000",
              borderWidth: 2,
              fill: false,
              data: timeSpacedA.map((v) => barValue(v)),
            },
          ],
        }}
        options={{
          responsive: true,
          legend: {
            display: false,
          },
          scales: {
            xAxes: [
              {
                display: true,
                ticks: {
                  maxTicksLimit: latestHour - earliestHour,
                },
              },
            ],
            yAxes: [
              {
                display: true,
                type: "linear",
              },
            ],
          },
        }}
      />
    </div>
  );
};

export default TestA;
