import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import Typography from "@material-ui/core/Typography";
import Select from "@material-ui/core/Select";
import apiManager from "../api/apiManager";
import moment from "moment";

const AssignmentEditForm = (props) => {
  const chosenRoute = props.chosenRoute;
  const handleChosenRouteChange = props.handleChosenRouteChange;
  const [assignment, setAssignment] = useState({
    startTime: "",
    endTime: "",
    driver_id: props.match.params.driverId,
    vehicle_id: props.match.params.vehicleId,
    route_id: props.match.params.routeId,
    date_id: props.chosenDate,
  });

  const [dates, setDates] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [vehicles, setVehicles] = useState([]);

  const getAssignment = (assignId) => {
    apiManager.getSingleType("assignments", assignId).then((assignment) => {
      setAssignment(assignment);
    });
  };

  console.log(assignment);

  const getAllDropDowns = () => {
    return (
      apiManager.getType("dates").then((r) => {
        r.sort((a, b) => (a.date > b.date ? 1 : -1));
        setDates(r);
      }),
      apiManager.getType("routes").then((r) => {
        r.sort((a, b) => (a.name > b.name ? 1 : -1));
        setRoutes(r);
      }),
      apiManager.getType("drivers").then((r) => {
        r.sort((a, b) => a.name.localeCompare(b.name));
        setDrivers(r);
      }),
      apiManager.getType("vehicles").then((r) => {
        r.sort((a, b) => (a.number > b.number ? 1 : -1)).sort((a, b) =>
          a.company.localeCompare(b.company)
        );
        setVehicles(r);
      })
    );
  };

  const handleAssignmentChange = (e) => {
    const stateToChange = { ...assignment };
    stateToChange[e.target.id] = e.target.value;
    setAssignment(stateToChange);
  };

  useEffect(() => {
    getAssignment(props.match.params.assignmentId);
    getAllDropDowns();
  }, [props.match.params.assignmentId]);

  const submit = () => {
    const editedAssignment = {
      id: props.match.params.assignmentId,
      startTime: assignment.startTime,
      endTime: assignment.endTime,
      driverId: assignment.driver_id,
      vehicleId: assignment.vehicle_id,
      routeId: assignment.route_id,
      dateId: assignment.date_id,
    };
    apiManager
      .updateType("assignments", editedAssignment)
      .then(() => props.history.push(`/routeview`));
  };

  // get all drivers, check if driver already in system, post if not, send to route view
  const handleSubmit = (e) => {
    assignment.route_id = chosenRoute;
    e.preventDefault();
    apiManager
      .getAssignmentsByDateDriver(assignment.date_id, assignment.driver_id)
      .then((assignments) => {
        if (assignments.length > 0) {
          alert("This driver has already been assigned on this day.");
        } else {
          apiManager
            .addType("assignments", assignment)
            .then(() => props.history.push(`/route/view`));
        }
      });
  };

  return (
    <>
      <Typography component="h1" variant="h5" className="page-header">
        Edit Assignment
      </Typography>
      <form className="drop-downs" onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <InputLabel>Driver:</InputLabel>
            <Select
              id="driver_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value={assignment.driver_id}
            >
              <option aria-label="None" value="" data-name="">
                Choose Driver
              </option>
              {drivers ? (
                drivers.map((driver) => (
                  <option key={driver.id} value={parseInt(driver.id)}>
                    {driver.name}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Vehicle:</InputLabel>
            <Select
              id="vehicle_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value={assignment.vehicle_id}
            >
              <option aria-label="None" value="" data-name="">
                Choose Vehicle
              </option>
              {vehicles ? (
                vehicles.map((vehicle) => (
                  <option key={vehicle.id} value={parseInt(vehicle.id)}>
                    {vehicle.company} {vehicle.number}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
          <Grid item xs={12} md={3}>
            <InputLabel>Route:</InputLabel>
            <Select
              id="route_id"
              native
              onChange={handleAssignmentChange}
              fullWidth
              required
              value={assignment.route_id}
            >
              <option aria-label="None" value="" data-name="">
                Choose Route
              </option>
              {routes ? (
                routes.map((route) => (
                  <option key={route.id} value={parseInt(route.id)}>
                    {route.name} {route.description}
                  </option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Grid>
        </Grid>
        <div className="submit-button">
          <Button type="submit" variant="contained" color="primary">
            Submit
          </Button>
        </div>
      </form>
    </>
  );
};

export default AssignmentEditForm;
