import React, { useState, useEffect } from "react";
import LoginForm from "../forms/loginForm";
import apiManager from "../api/apiManager";

const Login = (props) => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [failedLogin, setFailedLogin] = useState(false);

  const handleFieldChange = (evt) => {
    const stateToChange = { ...formData };
    stateToChange[evt.target.id] = evt.target.value;
    setFormData(stateToChange);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    const user = {
      username: formData.username,
      password: formData.password,
    };

    apiManager.login(user).then((resp) => {
      if ("token" in resp) {
        props.setUserToken(resp);
        props.history.push("/");
      }
      // If there is no token,
      // the login was unsuccessful,
      // and so an error message is displayed
      else {
        setFailedLogin(true);
      }
    });
  };

  useEffect(() => {}, [failedLogin]);

  const useStyles = makeStyles((theme) => ({
    root: {
      height: "100vh",
      zIndex: "3",
      position: "relative",
    },
    paper: {
      margin: theme.spacing(8, 4),
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: "100%", // Fix IE 11 issue.
      marginTop: theme.spacing(1),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));

  return (
    <>
      <div onSubmit={handleLogin}>
      <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Typography component="h1" variant="h5">
            Login
          </Typography>
          <form className={classes.form} noValidate>
            {/* {props.failedLogin ? (
              <Message negative>
                <p>
                  The username or password you entered is incorrect, please try
                  again.
                </p>
              </Message>
            ) : (
              <></>
            )} */}
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
              onChange={props.handleFieldChange}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={props.handleFieldChange}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item>
                <Link to="/register" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
      </Grid>
    </Grid>
      </div>
    </>
  );
};

export default Login;
