import React, { useState, useEffect } from "react";
import ShuttleForm from "./forms/shuttleForm"
import RouteForm from "./forms/routeForm"
import DriverForm from "./forms/driverForm"
import VehicleForm from "./forms/vehicleForm"
import AssignmentForm from "./forms/assignmentForm"
import apiManager from "./api/apiManager";
import "./styles.css";

const EventTranspoManager = (props) => {
  const isAuthenticated = () => sessionStorage.getItem("credentials") !== null;
  const [hasUser, setHasUser] = useState(isAuthenticated());

  const setUser = (user) => {
    sessionStorage.setItem("credentials", JSON.stringify(user));
    setHasUser(isAuthenticated());
  };

  const clearUser = () => {
    sessionStorage.clear();
    setHasUser(isAuthenticated());
  };

  const [dates, setDates] = useState([]);
  const [chosenDate, setChosenDate] = useState(1);

  const getDates = (type) => {
    apiManager.getType(type).then((APIResult) => {
      setDates(APIResult);
    });
  };

  useEffect(() => {
    getDates("dates");
  }, []);

  const handleDateChange = (event) => {
    const dateId = parseInt(event.target.value);
    setChosenDate(dateId);
  };

  return (
    <BrowserRouter>
      <Route exact path="/" render={() => <Redirect to="/login" />} />
      <Route
        exact
        path="/shuttle/form"
        render={() => (hasUser ? <ShuttleForm /> : <Redirect to="/login" />)}
      />
      <Route
        exact
        path="/route/form"
        render={() => (hasUser ? <RouteForm /> : <Redirect to="/login" />)}
      />
      <Route
        exact
        path="/driver/form"
        render={(props) =>
          hasUser ? <DriverForm {...props} /> : <Redirect to="/login" />
        }
      />
      <Route
        exact
        path="/assignment/form"
        render={(props) =>
          hasUser ? <AssignmentForm {...props} /> : <Redirect to="/login" />
        }
      />
      <Route
        exact
        path="/vehicle/form"
        render={(props) =>
          hasUser ? <VehicleForm {...props} /> : <Redirect to="/login" />
        }
      />
    </BrowserRouter>
  );
};

export default EventTranspoManager;
