import React, { useState, useEffect } from "react";
import { BrowserRouter, useHistory, Route, Redirect } from "react-router-dom";
import Nav_Bar from "./Navbar";
import EntryForm from "./forms/EntryForm";

const EventTranspoTracker = (props) => {
  let history = useHistory();
  const isAuthenticated = () => sessionStorage.getItem("token") !== null;
  const [hasUser, setHasUser] = useState(isAuthenticated());
  const [userInfo, setUserInfo] = useState({});

  const setUserToken = (resp) => {
    sessionStorage.setItem("token", resp.token);
    setHasUser(isAuthenticated());
  };

  const clearUser = () => {
    sessionStorage.clear();
    setHasUser(isAuthenticated());
  };

  const [entry, setEntry] = useState({
    created_at: "",
    attendee_count: "",
    vehicle_number: "",
    event_id: 1,
    location_id: "",
    route_id: 1,
    user_id: 1,
  });

  const handleEntryChange = (e) => {
    const stateToChange = { ...entry };
    stateToChange[e.target.id] = e.target.value;
    setEntry(stateToChange);
  };

  return (
    <BrowserRouter>
      <Nav_Bar />
      <Route
        exact
        path="/entry/form"
        render={(props) => <EntryForm handleEntryChange={handleEntryChange} {...props} />}
      />
    </BrowserRouter>
  );
};

export default EventTranspoTracker;
