import React, { useState, useEffect } from "react";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import apiManager from "../api/apiManager";

const EntryForm = (props) => {
  const userLocation = props.userLocation;
  const handleUserLocationChange = props.handleUserLocationChange;
  const [entry, setEntry] = useState({
    attendee_count: "",
    vehicle_number: "",
    event_id: 1,
    location_id: userLocation,
    route_id: 3,
    user_id: 1,
  });
  const [locationsDropDown, setLocationsDropDown] = useState([]);

  const handleEntryChange = (e) => {
    const stateToChange = { ...entry };
    stateToChange[e.target.id] = e.target.value;
    setEntry(stateToChange);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // const token = sessionStorage.getItem("token");
    apiManager.postEntry(entry).then(() => {
      setEntry({
        attendee_count: "",
        vehicle_number: "",
        event_id: 1,
        location_id: "",
        route_id: 1,
        user_id: 1,
      });
    });
  };

  useEffect(() => {
    apiManager
      .getAllLocations()
      .then((locations) =>
        locations.sort((a, b) => a.name.localeCompare(b.name))
      )
      .then(setLocationsDropDown);
  }, []);

  return (
    <>
      <div className="event-form-page">
        <h1 className="event_header">New Event Form</h1>
        <form className="event_form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={3}>
              <TextField
                required
                type="number"
                id="attendee_count"
                label="Attendee Count"
                fullWidth
                onChange={handleEntryChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                required
                id="vehicle_number"
                label="Vehicle Number"
                fullWidth
                onChange={handleEntryChange}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <InputLabel htmlFor="age-native-simple">Location: </InputLabel>
              <Select
                id="location_id"
                native
                onChange={handleUserLocationChange}
                fullWidth
                required
                label="Image URL"
                // value={userLocation}
              >
                <option aria-label="None" value="">
                  Choose Location
                </option>
                {locationsDropDown ? (
                  locationsDropDown.map((location) => (
                    <option key={location.id} value={parseInt(location.id)}>
                      {location.name}
                    </option>
                  ))
                ) : (
                  <></>
                )}
              </Select>
            </Grid>
          </Grid>
          <Grid>
            <button onClick={handleSubmit} type="submit" content="Submit Entry">
              Submit
            </button>
          </Grid>
        </form>
      </div>
    </>
  );
};

export default EntryForm;
