import React from "react";
import { Bar } from "react-chartjs-2";

const RouteGraph = (props) => {
  const entries = props.entriesByDate;

  let state = {}

  if (entries.length != 0) {
  state = {
    labels: entries.map(entry => entry.time.slice(0, -3)),
    datasets: [
      {
        backgroundColor: "rgba(75,192,192,1)",
        borderColor: "rgba(0,0,0,1)",
        borderWidth: 2,
        data: entries.map((entry) => entry.attendee_count),
      },
    ],
  };
}

  return (
    <div>
      <Bar
        data={state}
        options={{
          title: {
            display: true,
            text: "Attendee counts over time",
            fontSize: 20,
          },
          legend: {
            display: false,
          },
          scales: {
            xAxes: [
              {
                // labels: entries.map((entry) => entry.time),
                type: "time",
                distribution: "linear",
              },
            ],
            yAxes: [
              {
                ticks: {
                  beginAtZero: true,
                },
              },
            ],
          },
        }}
      />
    </div>
  );
};

export default RouteGraph;
