import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Redirect } from "react-router-dom";
import NavBar from "./navbar";
import EventTranspoTracker from "./eventTranspoTracker";
import EventTranspoManager from "./eventTranspoManager";
import Login from "./auth/login";
import Register from "./auth/register";
import EntryForm from "./forms/entryForm";
import EntryEditForm from "./forms/entryEditForm";
import apiManager from "./api/apiManager";
import RouteReport from "./reports/routeReport";
import Log from "./reports/log";
import "./styles.css";

const AppViews = (props) => {
  const isAuthenticated = () => sessionStorage.getItem("token") !== null;
  const [hasUser, setHasUser] = useState(isAuthenticated());

  const setUserToken = (resp) => {
    sessionStorage.setItem("token", resp.token);
    sessionStorage.setItem("userID", resp.user_id);
    setHasUser(isAuthenticated());
  };

  const clearUser = () => {
    sessionStorage.clear();
    setHasUser(isAuthenticated());
  };

  const [mode, setMode] = useState(true);
  const handleModeChange = () => {
    setMode(!mode);
  };

  return (
    <BrowserRouter>
      <>
        {hasUser ? (
          <>
            <NavBar
              hasUser={hasUser}
              clearUser={clearUser}
              mode={mode}
              handleModeChange={handleModeChange}
              {...props}
            />
            <>
              {mode == false ? (
                <EventTranspoTracker
                  hasUser={hasUser}
                  clearUser={clearUser}
                  mode={mode}
                  handleModeChange={handleModeChange}
                  {...props}
                />
              ) : (
                <EventTranspoManager
                  hasUser={hasUser}
                  clearUser={clearUser}
                  mode={mode}
                  handleModeChange={handleModeChange}
                  {...props}
                />
              )}
            </>
          </>
        ) : (
          <>
            <Route exact path="/" render={() => <Redirect to="/login" />} />
            <Route
              exact
              path="/login"
              render={
                (props) => (
                  hasUser ? (
                    <Redirect to="/" />
                  ) : (
                  <Login setUserToken={setUserToken} {...props} />
                )
                )
              }
            />
            <Route
              exact
              path="/register"
              render={
                (props) => (
                  hasUser ? (
                    <Redirect to="/" />
                  ) : (
                  <Register setUserToken={setUserToken} {...props} />
                )
                )
              }
            />
          </>
        )}
      </>
    </BrowserRouter>
  );
};

export default AppViews;
