import React, { useState, useEffect } from "react";
import apiManager from "./api/apiManager";
import EntryRow from "./EntryRow";

const LocationLog = (props) => {
  const locations = props.locations;
  const chosenLocation = props.chosenLocation;

  const getChosenLocationName = (locationID) => {
    apiManager.getSingleLocation(locationID).then((r) => {
      setLocation(r);
    });
  };

  useEffect(() => {
    getChosenLocationName(chosenLocation);
  }, [chosenLocation]);

  return (
    <>
      <div>
        <select
          id="locationId"
          onChange={props.handleLocationChange}
          value={props.chosenLocation}
          className="location_picker"
        >
          {props.locations.map((location) => (
            <option key={location.id} value={location.id} name={location.name}>
              {location.name}
            </option>
          ))}
        </select>
      </div>
      <div className="location_log_header">
        <h2>Entries from {chosenLocation.name}</h2>
      </div>
      <div className="list-container">
        {locations
          .filter((location) => location.id === chosenLocation)
          .map((location) => (
            <EntryRow key={location.id} location={location} {...props} />
          ))}
      </div>
    </>
  );
};

export default LocationLog;
